mapper.select:
select movie_seq movieSeq, rank, title, rank_date rankDate from movie
where title like '%${searchWord}%'
limit #{startRow}, #{pageSize}

select movie_seq movieSeq, rank, title, rank_date rankDate
from movie
where movie_seq = #{value}

SELECT movie_seq movieSeq, rank, title, rank_date rankDate
FROM movie WHERE title like '%${title}%'


        AND title like #{title}


        AND ost like #{ost.seq}


        AND rank_date like '${rankDate}%'

mapper.update:
update movie
rank=#{rank}, title=#{title}, rank_date=#{rank_date},
where movie_seq=#{movie_seq}
